namespace MU\FilesModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\FilesModule\Entity\CollectionEntity;

/**
 * Event base class for filtering collection processing.
 */
class AbstractFilterCollectionEvent extends Event
{
    /**
     * @var CollectionEntity Reference to treated entity instance.
     */
    protected $collection;

    /**
     * @var array Entity change set for preUpdate events.
     */
    protected $entityChangeSet = [];

    /**
     * FilterCollectionEvent constructor.
     *
     * @param CollectionEntity $collection Processed entity
     * @param array $entityChangeSet Change set for preUpdate events
     */
    public function __construct(CollectionEntity $collection, $entityChangeSet = [])
    {
        $this->collection = $collection;
        $this->entityChangeSet = $entityChangeSet;
    }

    /**
     * Returns the entity.
     *
     * @return CollectionEntity
     */
    public function getCollection()
    {
        return $this->collection;
    }

    /**
     * Returns the change set.
     *
     * @return array
     */
    public function getEntityChangeSet()
    {
        return $this->entityChangeSet;
    }
}
