/**
 * Validator class for encapsulating entity validation methods.
 *
 * This is the base validation class for hookobject entities.
 */
class MUFiles_Entity_Validator_Base_Hookobject extends MUFiles_Validator
{
    /**
     * Performs all validation rules.
     *
     * @return mixed either array with error information or true on success
     */
    public function validateAll()
    {
        $errorInfo = array('message' => '', 'code' => 0, 'debugArray' => array());
        $dom = ZLanguage::getModuleDomain('MUFiles');
        if (!$this->isStringNotEmpty('workflowState')) {
            $errorInfo['message'] = __f('Error! Field value must not be empty (%s).', array('workflow state'), $dom);
            return $errorInfo;
        }
        if (!$this->isStringNotLongerThan('hookedModule', 50)) {
            $errorInfo['message'] = __f('Error! Length of field value must not be higher than %2$s (%1$s).', array('hooked module', 50), $dom);
            return $errorInfo;
        }
        if (!$this->isStringNotEmpty('hookedModule')) {
            $errorInfo['message'] = __f('Error! Field value must not be empty (%s).', array('hooked module'), $dom);
            return $errorInfo;
        }
        if (!$this->isStringNotLongerThan('hookedObject', 50)) {
            $errorInfo['message'] = __f('Error! Length of field value must not be higher than %2$s (%1$s).', array('hooked object', 50), $dom);
            return $errorInfo;
        }
        if (!$this->isStringNotEmpty('hookedObject')) {
            $errorInfo['message'] = __f('Error! Field value must not be empty (%s).', array('hooked object'), $dom);
            return $errorInfo;
        }
        if (!$this->isValidInteger('areaId')) {
            $errorInfo['message'] = __f('Error! Field value may only contain digits (%s).', array('area id'), $dom);
            return $errorInfo;
        }
        if (!$this->isNumberNotEmpty('areaId')) {
            $errorInfo['message'] = __f('Error! Field value must not be 0 (%s).', array('area id'), $dom);
            return $errorInfo;
        }
        if (!$this->isNumberNotLongerThan('areaId', 11)) {
            $errorInfo['message'] = __f('Error! Length of field value must not be higher than %2$s (%1$s).', array('area id', 11), $dom);
            return $errorInfo;
        }
        if (!$this->isStringNotLongerThan('url', 255)) {
            $errorInfo['message'] = __f('Error! Length of field value must not be higher than %2$s (%1$s).', array('url', 255), $dom);
            return $errorInfo;
        }
        if (!$this->isStringNotEmpty('url')) {
            $errorInfo['message'] = __f('Error! Field value must not be empty (%s).', array('url'), $dom);
            return $errorInfo;
        }
        if (!$this->isValidInteger('objectId')) {
            $errorInfo['message'] = __f('Error! Field value may only contain digits (%s).', array('object id'), $dom);
            return $errorInfo;
        }
        if (!$this->isNumberNotEmpty('objectId')) {
            $errorInfo['message'] = __f('Error! Field value must not be 0 (%s).', array('object id'), $dom);
            return $errorInfo;
        }
        if (!$this->isNumberNotLongerThan('objectId', 11)) {
            $errorInfo['message'] = __f('Error! Length of field value must not be higher than %2$s (%1$s).', array('object id', 11), $dom);
            return $errorInfo;
        }
    
        return true;
    }
    
    /**
     * Check for unique values.
     *
     * This method determines if there already exist hookobjects with the same hookobject.
     *
     * @param string $fieldName The name of the property to be checked
     * @return boolean result of this check, true if the given hookobject does not already exist
     */
    public function isUniqueValue($fieldName)
    {
        if ($this->entity[$fieldName] === '') {
            return false;
        }
    
        $entityClass = 'MUFiles_Entity_Hookobject';
        $serviceManager = ServiceUtil::getManager();
        $entityManager = $serviceManager->getService('doctrine.entitymanager');
        $repository = $entityManager->getRepository($entityClass);
    
        $excludeid = $this->entity['id'];
    
        return $repository->detectUniqueState($fieldName, $this->entity[$fieldName], $excludeid);
    }
    
    /**
     * Get entity.
     *
     * @return Zikula_EntityAccess
     */
    public function getEntity()
    {
        return $this->entity;
    }
    
    /**
     * Set entity.
     *
     * @param Zikula_EntityAccess $entity.
     *
     * @return void
     */
    public function setEntity(Zikula_EntityAccess $entity = null)
    {
        $this->entity = $entity;
    }
    
}
